Export.Routine:
Checkin ; execute github add,commit and push
s defgitrepo="" s defprojectdir="" s defgitpath=""
s:($D(^CTRL.Code("gitrepo"))) defgitrepo=^CTRL.Code("gitrepo") // "https://github.com/peteoh/srcctrl.git" s:($D(^CTRL.Code("projectdir"))) defprojectdir=^CTRL.Code("projectdir") //"C:\DEV\srcctrl" s:($D(^CTRL.Code("gitpath"))) defgitpath=^CTRL.Code("gitpath") // "C:\Git\Bin"
w !, "This routine will export code from this namespace and push to github" r !, "Are you sure (Y/n)?", cont w !
if ((cont = "Y") || (cont = "y") || (cont = "")) { w !, "GIT path ("_defgitpath_"):" r gitpath w !, "GIT repo ("_defgitrepo_"):" r gitrepo w !, "Local Dir ("_defprojectdir_"):" r projectdir
// if values are set then save to globals s:(gitpath'="") ^CTRL.Code("gitpath")=gitpath s:(gitrepo'="") ^CTRL.Code("gitrepo")=gitrepo s:(projectdir'="") ^CTRL.Code("projectdir")=projectdir
// if values are not set then get the default values (from globals) s:(gitpath="") gitpath = defgitpath s:(gitrepo="") gitrepo = defgitrepo s:(projectdir="") projectdir = defprojectdir
if ( (gitrepo'="") && (projectdir'="") && (gitpath'="") ) {
r !, "Enter GIT Username:", user
if (user'="") { write !,"Enter GIT Password: " use 0:(:/echo=0) set pass="" for read *c quit:c=13 set pass=pass_$c(c) write "*" use 0:(:/echo=1)
s userpass = user_":"_pass_"@"
s $p(gitrepo,"/",3) = userpass_$p(gitrepo,"/",3) } // w !, gitrepo
// Export the Code
// get last package list s defpackages="" s:($D(^CTRL.Code("packages"))) defpackages=^CTRL.Code("packages") // "PKG,PKG.SUBPKG" s:(defpackages="") defpackages = "ALL"
w !, "Export Packages - ALL for all packages ("_defpackages_"):" r packages
// if values are set then save to globals s:(packages'="") ^CTRL.Code("packages")=packages // if values are not set then get the default values (from globals) s:(packages="") packages = defpackages
s:(packages="ALL") packages=""
do ##Class(CTRL.Code).export(packages) set wrkdir = ##class(CTRL.Code).workdir() Set pout=wrkdir_"\git.out" Set perr=wrkdir_"\git.err"
// Now call GitHub add, commit, push
// Get the Message for the Commit Do { r !,!, "Enter the Git Message for Commit: ", cmtmsg } while (cmtmsg="")
w !,!," Updating GIT..."
Set git=##class(CTRL.GITClient).%New(gitpath,wrkdir,"","","",pout,perr,gitrepo)
set sout = ""
Do git.Add("",.sout,1)
Do git.Commit("",cmtmsg,.sout)
Do git.Push(.sout)
w !,!,"FINISHED."
} else { w !, "ERROR: you need to provide the git repo (eg https://github.com/name/repo.git)" W !, " and git binary folder (eg: c:\git\bin )" W !, " and local folder (eg: c:\localfolder )" }
}

Method.Implementation:
if ( $LG(masks,1) = "" )
{
	s exp = 1
}
else
{ 
	s exp = 0 
	for i=1:1:$LL(masks) 
	{ 
		s exp = exp || ( $LG(masks,i) = $e(code,1,$l($LG(masks,i)))) 
	}  
}
quit exp

	#define export(%code, %file) s sc = $system.OBJ.ExportUDL(%code, %file,"/diffexport") ##continue
								 w:sc !,code, " -> ", $piece(filename,..workdir(),2), " "  d:'sc $system.OBJ.DisplayError(sc)

	#define isGenerated(%code) ##class(%RoutineMgr).IsGenerated( %code )
	#define isPercented(%code) ("%" = $e(%code))
	#define isMapped(%code) ##class(%RoutineMgr).IsMapped( %code )
	#define log w !, code, " -> ", $piece(filename,..workdir(),2), " "

	#define mkdir(%filename) ##continue
			s path = ##class(%File).GetDirectory( %filename ) ##continue
			if '##class(%File).DirectoryExists( path ) { ##continue
				s sc = ##class(%File).CreateDirectoryChain( path ) ##continue
				w !, "mkdir ", path, " ", sc ##continue
			}



	w !,!,"#; Exporting to ", ..workdir(),!

	s masks = $LISTFROMSTRING(packagelist,",")

	#; classes
	s rs = ##class(%ResultSet).%New("%Dictionary.ClassDefinition:Summary")
	if rs.Execute() {

		while rs.%Next(){

			s code = rs.Name _ ".cls", isSystem = rs.System
			if ( 'system && isSystem ) continue
			if ( 'generated && $$$isGenerated( code ) ) continue
			if ( 'percent && $$$isPercented( code ) ) continue
			if ( 'mapped && $$$isMapped( code ) ) continue

			//POH allowing for a list of filters
			if '..pkgmatch( masks, code ) continue


			// got here so exporting

			s filename = ..filename( code )
			$$$mkdir( filename )
			//$$$log
			$$$export( code, filename )

		} s rs=""
	}

	#; routines
	s rs = ##class(%ResultSet).%New("%Routine:RoutineList")
	if rs.Execute() {

    	while rs.%Next() {

	 		s code = rs.Name
	 		//w code,!
	 		if ( 'generated && $$$isGenerated( code ) ) continue
	 		if ( 'percent && $$$isPercented( code ) ) continue
			if ( 'mapped && $$$isMapped( code ) ) continue
			//if ( '$find( code, mask ) ) continue
			if '..pkgmatch( masks, code ) continue

			s filename = ..filename( code )
			$$$mkdir( filename )
			//$$$log
			$$$export( code, filename )

	    } s rs=""
	}


	#; dfi
	do ..fixDashIntoName()
	#define export(%code,%file) s sc = ##class(%DeepSee.UserLibrary.Utils).%Export( %code, %file, 0 ) 

	s sql = "Select id, fullName as Name, documentName From %DeepSee_UserLibrary.FolderItem"
    s rs = ##class(%SQL.Statement).%ExecDirect( .stm, sql )
    while rs.%Next() {
		set code = rs.Name
		set filename = ..filename( code_".dfi" )
		set documentName = rs.documentName

		//if ( '$find($zcvt(documentName,"l"), mask ) ) continue
		if '..pkgmatch( masks, $zcvt(documentName,"l") ) continue
		if ($L(code,"$TRASH")>1) continue
		$$$mkdir( filename )

		if dfi{
			//$$$log
			$$$export( code, filename)
		}
		elseif ('dfi){
			set documentName = $replace(documentName,"/","-")
			set filename = $extract(filename,1,*-4)
			do ..fixXMLLine(documentName,filename_".xml")
		}

	} s rs=""

	w !,!, "#; Exported to ", ..workdir()

    Q 1

#define push(%dir) s dirs( $i( dirs ) ) = %dir
#define next(%i,%dir) s %i=$o( dirs( "" ), 1, %dir )  k:%i'="" dirs(%i)
#define isDirectory(%type) ( %type = "D" )
#define log w:('sc) !, $piece(filename,..workdir(),2), " " d:('sc) $system.OBJ.DisplayError(sc)

s sc = 1, dirs = "", dir = ..workdir() $$$push(dir)
if verbose w "#; Importing from ", dir,!
d ..setIgnore(.fm)
for { $$$next(i,dir) Q:i=""  Q:dir=""

	s rs = ##class(%File).FileSetFunc( dir, filemask, , 1 )

	while rs.%Next() {
		s filename = rs.Name

		if $$$isDirectory(rs.Type) {
			if recurse $$$push(filename)
			continue
		}
		// check file filter
		if ..inFilter(.fm,filename) continue

		s ext = $zcvt($p( filename, ".", * ),"l")

		if (ext = "dfi") {
			s sc = ##class(%DeepSee.UserLibrary.Utils).%Import( filename, 1, 0, 0, "", .dsloaded )

		} else {
			// load classes only
			s sc = $system.OBJ.Load( filename, "k-d", .err, .loaded)
		}

		if verbose $$$log

    }
	s flags = "cukbr-d/multicompile"
	s flags = "cukbr-d"
	// compile all the loaded classes to obey dependencies
	//if dir = ..workdir()
	//{
		s sc=$system.OBJ.CompileList( .loaded, flags,.err )
	//}
	//else
	//{
	//	set package = $REPLACE($REPLACE(dir,..workdir()_"/",""),"/",".")
	//	s sc=$system.OBJ.CompilePackage( package, flags,.err )
	//}

}

if verbose {

	if $D(err) {
		s i=$Order(err("")) 
		while i'="" {
		w !,err(i)
		s i=$O(err(i))
		}
	}

	w !,!,"#; Imported from ", ..workdir()
	}
Q sc

s gln = ..gln() s git = @gln@("git")
set:commitTo="" commitTo=1 if (git = 0) { set:(commitFrom="")||(commitFrom=1) commitFrom=0 set sc = ##class(sc.diff.gitLocal).buildDiff(..workdir(), "HEAD~"_commitTo, "HEAD~"_commitFrom, .items) return:$$$ISERR(sc) sc }elseif(git = 1) { set owner = @gln@("owner") set repository = @gln@("repository") set user = @gln@("user") set password = @gln@("password") set:commitFrom="" commitFrom=1
set sc = ##class(sc.diff.gitHub).Get(.items, owner, repository, user, password, commitFrom, commitTo) return:$$$ISERR(sc) sc }
if (filename="") { set filename=$G(@gln@("projectName"),"patch") set filename=filename_$p($zdt($h,8)," ")_$tr($p($p($zdt($h)," ",2),":",1,2),":")_".xml" } zw filename set sc = $system.OBJ.Export(.items,filename)
return sc


// working with .gitignore file
// gitignore consists of masks of filtering files
s filename=..workdir()_"/.gitignore"
if '##class(%File).Exists(filename) quit
s file=##class(%File).%New(filename)
s sc=file.Open("R")
if 'sc 
{
	if verbose d $System.OBJ.DisplayError(sc) return
}

While 'file.AtEnd
{
	s filtermask($I(filtermask))=file.ReadLine()
}
quit

	#define log(%dir,%sc) w !, "mkdir ", %dir, " ", sc

	s wd = ..workdir()

	if '##class(%File).DirectoryExists( wd ) {
		s sc = ##class(%File).CreateDirectoryChain( wd )
		$$$log(wd,sc)
	}

	s ext = $p( code, ".", * ), ext = $zcvt( ext, "l" )
	//#; for each type - different directory
    //
	//s:ext'="" wd = ##class(%File).NormalizeDirectory( ext, wd )

	#; directory must exist before any call (%File).NormalizeFilename( , wd)
	if '##class(%File).DirectoryExists( wd ) {
		s sc = ##class(%File).CreateDirectoryChain( wd )
		$$$log(wd,sc)
	}

	s filename = ##class(%File).NormalizeFilename( code, wd )
	#; lowercase file extension
	s $p(filename,".",*)=ext
	//B:code="DPRep.Rest.JSON.cls" "L"
	#; for *.cls and *.mac Package.Subpackage.ClassName.cls -> Folder/Subfolder/ClassName.cls
	if ( (ext="cls") || (ext="mac")) {
		s dirs = $piece( code, ".",1, *-2 ), dirs = $translate( dirs, ".", "/" )
		s relpath = dirs _ "/" _ $piece( code, ".", *-1, * ) ;
		s filename = ##class(%File).NormalizeFilename( relpath, wd )
	}
	Q filename

#define push(%dir) s dirs( $i( dirs ) ) = %dir #define next(%i,%dir) s %i=$o( dirs( "" ), 1, %dir ) k:%i'="" dirs(%i) #define isDirectory(%type) ( %type = "D" ) #define log w:('sc) !, $piece(filename,..workdir(),2)," " d:('sc) $system.OBJ.DisplayError( sc )
s sc = 1, dirs = "", dir = ..workdir() $$$push(dir) if verbose w "#; Importing updated from ", dir,! d ..setIgnore(.fm) for { $$$next(i,dir) Q:i="" Q:dir=""
s rs = ##class(%File).FileSetFunc( dir, filemask, , 1 )
while rs.%Next() { s filename = rs.Name
if $$$isDirectory( rs.Type ) { if ( recurse ) $$$push(filename) ;push directory continue }
// check file filter if ..inFilter(.fm,filename) continue
s filets = rs.DateModified s codename = ..codename( filename, .ext ) s codets = ..codets( codename, ext )
#; w !, filename, $c(9), "(", filets, ")" #; w !, codename, $c(9), "(", codets, ")"
if ( filets '] codets ) continue
if (ext = "dfi"){ s sc = ##class(%DeepSee.UserLibrary.Utils).%Import( filename, 1, 0, 0, "", .dsloaded ) } else {
#; drop existing code before import ( purge DateModified ) #; s:codets'="" sc = ##class(%RoutineMgr).Delete( codename ) k err s sc = $system.OBJ.Load( filename, qspec, .err, .loaded)
}
if verbose $$$log } } w:verbose !,"#; Imported updated from ", ..workdir() Q sc

s ext = $p( filename, ".", * ), ext = $zcvt( ext, "l" )
s path = ##class(%File).NormalizeDirectory( ext, ..workdir() )
s codename = $p( filename, path, 2 )
if ext = "dfi" {
	s fullname  = $tr( codename, "\", "/" ) ; return fullname for dfi in $$$IsWINDOWS
	Q $p( fullname, ".", 1, *-1 ) ;remove extension
}
if (ext ="cls")!(ext="int")!(ext="inc")!(ext="mac") s codename=$tr(codename,"\/","..")
Q codename

s ts = ""
if ext'="dfi" {
	s ts = ##class(%RoutineMgr).TS( codename )
} else {
	s sql="Select timeModified From %DeepSee_UserLibrary.FolderItem Where fullname = ?"
	s rs = ##class(%SQL.Statement).%ExecDirect( , sql, codename )
	if rs.%Next() {
		s utcts = rs.timeModified
		s utch = $zdth( utcts, 3, , 3 ) ;utc internal format
		s loch = $zdth( utch, -3 ) ; utc to local timezone
		s ts = $zdt( loch, 3, ,0 ) ; local timestamp*/
	}
}
Q $p( ts, "." ) ;remove ms

	s sql = "Select id, fullName as Name, documentName From %DeepSee_UserLibrary.FolderItem"
    s rs = ##class(%SQL.Statement).%ExecDirect( .stm, sql )
    while rs.%Next() {
		if $find(rs.Name, "-"){
			w rs.Name," -> "
			set item = ##class(%DeepSee.UserLibrary.FolderItem).%OpenId(rs.id)
			set item.name = $replace(item.name,"-"," ")
			w item.name,!
			do item.%Save()
		}
    }
    k rs

	new $Namespace
	set ns = $Namespace // ought to be package home namespace!
	set st = $$$OK

    write !, "Installing CTRL application to " _ ns

    do ..CreateAllNamespace()

    write !, "Mapping CTRL package into all namespaces:"
    set st = ..Map(ns)
    if ($$$ISERR(st)) {
        write !, "ERROR: CTRL package was not mapped into %ALL namespace !!!."
    } else {
	    write !, "CTRL package successfully mapped into all namespaces."
    }

    return st

	new $Namespace
    set $Namespace = "%SYS"
	set ns = "%All"
    set st = $$$OK

	if ('##Class(Config.Namespaces).Exists(ns)) {

        set dbPrefix = $case(..#iscProductVersion >= 4, 1: "IRIS", : "CACHE")
        set Properties("Globals") = dbPrefix _ "TEMP"
        set Properties("Library") = dbPrefix _ "LIB"
        set Properties("Routines") = dbPrefix _ "TEMP"
        set Properties("SysGlobals") = dbPrefix _ "SYS"
        set Properties("SysRoutines") = dbPrefix _ "SYS"
        set Properties("TempGlobals") = dbPrefix _ "TEMP"

		set st = ##Class(Config.Namespaces).Create(ns, .Properties)
		if ($$$ISERR(st)) {
        	do $System.Status.DisplayError(st)
    	} else {
        	write !, "%All namespace is created."
        }

    }

    return st

	new $Namespace
    set $Namespace = "%SYS"
    set st = $$$OK

    set mapTo = $LISTBUILD("%All")
    do ##Class(Config.Namespaces).Get(fromNS, .InstallNSProps)
    set Properties("Database") = $get(InstallNSProps("Routines"))
    set ptr = 0
    while $LISTNEXT(mapTo, ptr, namespace) {
        continue:(fromNS = namespace)
        continue:('##Class(Config.Namespaces).Exists(namespace))
        write " ", namespace
        if ('##Class(Config.MapPackages).Exists(namespace, "CTRL")) {
        	set st1 = ##Class(Config.MapPackages).Create(namespace, "CTRL", .Properties)
        }
        if ('##Class(Config.MapRoutines).Exists(namespace, "CTRLCheckin.mac")) {
	        set st2 = ##Class(Config.MapRoutines).Create(namespace, "CTRL.Checkin.mac", .Properties)
        }
        set st = $$$ADDSC(st,$$$ADDSC($get(st1,$$$OK),$get(st2,$$$OK)))
    }
    return st

	new $Namespace
    set $Namespace = "%SYS"
    set st = $$$OK

	set mapTo = $LISTBUILD("%All")
    set ptr = 0
    while $LISTNEXT(mapTo, ptr, namespace) {
	    continue:(fromNS = namespace)
	    continue:('##Class(Config.Namespaces).Exists(namespace))
        write " ", namespace
        if (##Class(Config.MapPackages).Exists(namespace, "CTRL")) {
        	set st1 = ##Class(Config.MapPackages).Delete(namespace, "CTRL", .Properties)
        }
        if (##Class(Config.MapRoutines).Exists(namespace, "CTRLCheckin.mac")) {
	        set st2 = ##Class(Config.MapRoutines).Delete(namespace, "CTRL.Checkin.mac", .Properties)
        }
        set st = $$$ADDSC(st,$$$ADDSC($get(st1,$$$OK),$get(st2,$$$OK)))
    }
    return st

if pLevel >= ..#LOGLEVEL {
	write:pNL !
	write pMsg
}
quit

Export.Routine:
Pull ; execute github pull
s defgitrepo="" s defprojectdir="" s defgitpath="" s defgitusername = "" s defgittoken = ""
s:($D(^CTRL.Code("gitrepo"))) defgitrepo=^CTRL.Code("gitrepo") // "https://github.com/Buddybet-Group/BuddyBet-IRISv2.0.git" s:($D(^CTRL.Code("projectdir"))) defprojectdir=^CTRL.Code("projectdir") //"/CoreDatabase/GIT/BuddyBet-IRISv2.0/" s:($D(^CTRL.Code("gitpath"))) defgitpath=^CTRL.Code("gitpath") // "/usr/bin/" s:($D(^CTRL.Code("gitusername"))) defgitusername=^CTRL.Code("gitusername") // "Buddybetadmin" s:($D(^CTRL.Code("gittoken"))) defgittoken=^CTRL.Code("gittoken") // "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"
w !, "This routine assumes the git repo has already been cloned, and will now just pull from github" r !, "Are you sure (Y/n)?", cont w !
if ((cont = "Y") || (cont = "y") || (cont = "")) { w !, "GIT path ("_defgitpath_"):" r gitpath w !, "GIT repo ("_defgitrepo_"):" r gitrepo w !, "Local Dir ("_defprojectdir_"):" r projectdir w !, "Username ("_defgitusername_"):" r gitusername w !, "GIT Token (*****):" r gittoken
// if values are set then save to globals s:(gitpath'="") ^CTRL.Code("gitpath")=gitpath s:(gitrepo'="") ^CTRL.Code("gitrepo")=gitrepo s:(projectdir'="") ^CTRL.Code("projectdir")=projectdir s:(gitusername'="") ^CTRL.Code("gitusername")=gitusername s:(gittoken'="") ^CTRL.Code("gittoken")=gittoken
// if values are not set then get the default values (from globals) s:(gitpath="") gitpath = defgitpath s:(gitrepo="") gitrepo = defgitrepo s:(projectdir="") projectdir = defprojectdir s:(gitusername="") gitusername = defgitusername s:(gittoken="") gittoken = defgittoken
if ( (gitrepo'="") && (projectdir'="") && (gitpath'="") && (gitusername'="") && (gittoken'="") ) {
//write !,"Enter GIT Token (******): " //use 0:(:/echo=0) //set pass="" //for read *c quit:c=13 set pass=pass_$c(c) write "*" //use 0:(:/echo=1)
// if password is empty then use the default //s:(pass'="") ^CTRL.Code("gittoken")=pass //s:(pass="") pass = defgittoken
s userpass = user_":"_gittoken_"@"
s $p(gitrepo,"/",3) = userpass_$p(gitrepo,"/",3)
set wrkdir = ##class(CTRL.Code).workdir() Set pout=wrkdir_"/git.out" Set perr=wrkdir_"/git.err"
// Now call GitHub add, commit, push
w !,!," Pulling GIT..."
Set git=##class(CTRL.GITClient).%New(gitpath,wrkdir,"","","",pout,perr,gitrepo)
set sout = ""
Do git.Pull(.sout)
w !,!,"FINISHED." _sout
} else { w !, "ERROR: you need to provide the git repo (eg https://github.com/name/repo.git)" W !, " and git binary folder (eg: c:\git\bin )" W !, " and local folder (eg: c:\localfolder )" }
}
